@startchen

entity Student {
  student_id <<key>>
  name
  email
  password
  phone
  enrollment_date
  student_status
}

entity Instructor {
  instructor_id <<key>>
  name
  email
  password
  phone
  specialization
  instructor_status
}

entity Course {
  course_id <<key>>
  course_name
  course_description
  credit_hours
  start_date
  end_date
  course_status
}

entity Assessment {
  assessment_id <<key>>
  course_id <<foreign>>
  assessment_name
  assessment_type
  total_marks
  assessment_status
}

entity Submission {
  submission_id <<key>>
  assessment_id <<foreign>>
  student_id <<foreign>>
  submission_date
  marks_obtained
  submission_status
}

entity Content {
  content_id <<key>>
  course_id <<foreign>>
  content_title
  content_type
  upload_date
  content_status
}

relationship TEACHES_COURSE {
}
relationship ENROLLS_IN {
  enrollment_date
  enrollment_status
}
relationship HAS_ASSESSMENT {
}
relationship SUBMITS {
}
relationship BELONGS_TO {
}
relationship HAS_CONTENT {
}
relationship COMMUNICATES_WITH {
  message_content
  message_date
  communication_status
}

TEACHES_COURSE -1- Instructor
TEACHES_COURSE -N- Course

ENROLLS_IN -1- Student
ENROLLS_IN -N- Course

HAS_ASSESSMENT -1- Course
HAS_ASSESSMENT -N- Assessment

SUBMITS -1- Student
SUBMITS -N- Submission

BELONGS_TO -1- Assessment
BELONGS_TO -N- Submission

HAS_CONTENT -1- Course
HAS_CONTENT -N- Content

COMMUNICATES_WITH -N- Instructor
COMMUNICATES_WITH -N- Student

@endchen

